import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useState } from 'react';
import { Redirect } from 'react-router';

function RegistrationForm() {

  const [redirect, setRedirect] = useState(false);

  const [post, setPost] = useState({
    "email": "",
    "password": "",
    "first_name": "",
    "last_name": "",
    "phone": "",
    "position": ""
  })
  
  const handleInput = (event) => {
    setPost({...post, [event.target.name]: event.target.value})
  }

  function handleSubmit(event) {
    event.preventDefault()
    axios.post('http://127.0.0.1:8000/api/register/', post)
    .then(responce => console.log(responce))
    .catch(err => console.log(err))

    setRedirect(true);
  }

  if (redirect) {
    return <Redirect to="/"/>;
  }

  return (
    <div className="registration-form">
      <div className="header">
      <img src={require('./assets/image2.png')} alt="Логотип компании" className="logo" />
        <Link to="/" className="home-link">Уже есть аккаунт? Войти</Link>
      </div>
      <div className="content">
        <p className='biggest-text'>РЕГИСТРАЦИЯ</p>
        <div className="form-wrapper">
        <form onSubmit={handleSubmit}>
            <div className="input-wrapper">
              <input type="text" onChange={handleInput} placeholder="Имя" name="first_name"/>
            </div>
            <div className="input-wrapper">
              <input type="text" onChange={handleInput} placeholder="Фамилия" name="last_name"/>
            </div>
            <div className="input-wrapper">
              <input type="text" onChange={handleInput} placeholder="Должность" name="position"/>
            </div>
            <div className="input-wrapper">
              <input type="tel" onChange={handleInput} placeholder="Телефон" name="phone"/>
            </div>
            <div className="input-wrapper">
              <input type="email" onChange={handleInput} placeholder="Почта" name="email"/>
            </div>
            <div className="input-wrapper">
              <input type="password" onChange={handleInput} placeholder="Пароль" name="password"/>
            </div>
            <button className="register-button">Зарегистрироваться</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default RegistrationForm;
